import{_ as t,c as n,a2 as r,o as a}from"./chunks/framework.YPpNXepj.js";const g=JSON.parse('{"title":"Create a Content JSON","description":"","frontmatter":{},"headers":[],"relativePath":"content/json/create.md","filePath":"content/json/create.md"}'),i={name:"content/json/create.md"};function o(l,e,s,u,h,d){return a(),n("div",null,e[0]||(e[0]=[r('<h1 id="create-a-content-json" tabindex="-1">Create a Content JSON <a class="header-anchor" href="#create-a-content-json" aria-label="Permalink to &quot;Create a Content JSON&quot;">​</a></h1><h2 id="essential-information" tabindex="-1">Essential Information <a class="header-anchor" href="#essential-information" aria-label="Permalink to &quot;Essential Information&quot;">​</a></h2><p>A Content JSON is a JSON file used to configure an entire Content instance. The easiest way to create this JSON file is by using our dedicated Content JSON Editor available on our official website <a href="https://onelauncher.zmito.eu/#/content-editor" target="_blank" rel="noreferrer">here</a>.</p><h2 id="content-json-properties" tabindex="-1">Content JSON Properties <a class="header-anchor" href="#content-json-properties" aria-label="Permalink to &quot;Content JSON Properties&quot;">​</a></h2><h3 id="id" tabindex="-1">id <a class="header-anchor" href="#id" aria-label="Permalink to &quot;id&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> ID of the Content Instance, only lowercase letters, numbers or hyphens. Cannot be changed in the future.</li></ul><h3 id="name" tabindex="-1">name <a class="header-anchor" href="#name" aria-label="Permalink to &quot;name&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> Display name of the Content.</li></ul><h3 id="maintenance" tabindex="-1">maintenance? <a class="header-anchor" href="#maintenance" aria-label="Permalink to &quot;maintenance?&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> False</li><li><strong>Description:</strong> Maintenance mode will disable the ability to play the Content until you disable it.</li></ul><h3 id="bannerurl" tabindex="-1">bannerURL <a class="header-anchor" href="#bannerurl" aria-label="Permalink to &quot;bannerURL&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> URL of the image to be used as the Content banner on the main page.</li></ul><h3 id="bgurl" tabindex="-1">bgURL <a class="header-anchor" href="#bgurl" aria-label="Permalink to &quot;bgURL&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> URL of the image or video to be used as background on the Content page.</li></ul><h3 id="titleurl" tabindex="-1">titleURL? <a class="header-anchor" href="#titleurl" aria-label="Permalink to &quot;titleURL?&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> False</li><li><strong>Description:</strong> URL of the image to be used as the Content title. If none is provided, the Content name will be used as the title.</li></ul><h3 id="mcver" tabindex="-1">mcVer <a class="header-anchor" href="#mcver" aria-label="Permalink to &quot;mcVer&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> Minecraft version of the Content instance.</li></ul><h3 id="mcloader" tabindex="-1">mcLoader <a class="header-anchor" href="#mcloader" aria-label="Permalink to &quot;mcLoader&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> Mod Loader of the Content instance.</li></ul><h3 id="mcloaderver" tabindex="-1">mcLoaderVer? <a class="header-anchor" href="#mcloaderver" aria-label="Permalink to &quot;mcLoaderVer?&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> False</li><li><strong>Description:</strong> Specific version of the Mod Loader. If unspecified, the latest available version will be used.</li></ul><h3 id="mcserverip" tabindex="-1">mcServerIP? <a class="header-anchor" href="#mcserverip" aria-label="Permalink to &quot;mcServerIP?&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> False</li><li><strong>Description:</strong> The public IP address of the Minecraft server will be used to indicate its online status and player count.</li></ul><h3 id="updatever" tabindex="-1">updateVer <a class="header-anchor" href="#updatever" aria-label="Permalink to &quot;updateVer&quot;">​</a></h3><ul><li><strong>Type:</strong> floating-point</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> Version number of the new update. Must be higher than the preceding version.</li></ul><h3 id="updatefileurl" tabindex="-1">updateFileUrl <a class="header-anchor" href="#updatefileurl" aria-label="Permalink to &quot;updateFileUrl&quot;">​</a></h3><ul><li><strong>Type:</strong> string</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> URL of the ZIP file containing all the necessary files (mods, settings, shaders) for your Content instace.&quot;</li></ul><h3 id="updatekeepedfiles" tabindex="-1">updateKeepedFiles <a class="header-anchor" href="#updatekeepedfiles" aria-label="Permalink to &quot;updateKeepedFiles&quot;">​</a></h3><ul><li><strong>Type:</strong> array</li><li><strong>Required:</strong> True</li><li><strong>Description:</strong> Array containing the names of files and folders to be kept.</li></ul><h2 id="content-json-example" tabindex="-1">Content JSON Example <a class="header-anchor" href="#content-json-example" aria-label="Permalink to &quot;Content JSON Example&quot;">​</a></h2><p>If you&#39;d like to see an example of a Content JSON, please refer to this <a href="./../create-content.html#content-instance-example">specific point</a>.</p><h2 id="publishing-a-content-json" tabindex="-1">Publishing a Content JSON <a class="header-anchor" href="#publishing-a-content-json" aria-label="Permalink to &quot;Publishing a Content JSON&quot;">​</a></h2><p>For more information on <strong>how to publish your Content JSON</strong>, continue to the <a href="/content/json/publish.html">next page</a>.</p>',34)]))}const p=t(i,[["render",o]]);export{g as __pageData,p as default};
